INSERT INTO `core_email_template` VALUES (3,'Forgot Password','{{template config_path=\"design/email/header\"}}\r\n{{inlinecss file=\"email-inline.css\"}}\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n    <tr>\r\n        <td class=\"action-content\">\r\n            <h1>{{htmlescape var=$customer.name}},</h1>\r\n            <p>There was recently a request to change the password for your account.</p>\r\n            <p>If you requested this password change, please reset your password here:</p>\r\n            <table cellspacing=\"0\" cellpadding=\"0\" class=\"action-button\" >\r\n                <tr>\r\n                    <td>\r\n                        <a href=\"{{store url=\"customer/account/resetpassword/\" _query_id=$customer.id _query_token=$customer.rp_token}}\"><span>Reset Password</span></a>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n            <p>If you did not make this request, you can ignore this message and your password will remain the same.</p>\r\n        </td>\r\n    </tr>\r\n</table>\r\n\r\n{{template config_path=\"design/email/footer\"}}',NULL,2,'Password Reset Confirmation for {{var customer.name}}',NULL,NULL,'2018-07-07 02:28:38','2018-07-07 02:28:38','customer_password_forgot_email_template','{\"store url=\\\"\\\"\":\"Store Url\",\"var logo_url\":\"Email Logo Image Url\",\"var logo_alt\":\"Email Logo Image Alt\",\"htmlescape var=$customer.name\":\"Customer Name\",\"store url=\\\"customer/account/resetpassword/\\\" _query_id=$customer.id _query_token=$customer.rp_token\":\"Reset Password URL\"}'),(4,'New Order','{{template config_path=\"design/email/header\"}}\r\n{{inlinecss file=\"email-inline.css\"}}\r\n\r\n<table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n    <tr>\r\n        <td>\r\n            <table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n                <tr>\r\n                    <td class=\"email-heading\">\r\n                        <h1>Thank you for your order from {{var store.getFrontendName()}}.</h1>\r\n                        <p>Once your package ships we will send an email with a link to track your order. Your order summary is below. Thank you again for your business.</p>\r\n                    </td>\r\n                    <td class=\"store-info\">\r\n                        <h4>Order Questions?</h4>\r\n                        <p>\r\n                            {{depend store_phone}}\r\n                            <b>Call Us:</b>\r\n                            <a href=\"tel:{{var phone}}\">{{var store_phone}}</a><br>\r\n                            {{/depend}}\r\n                            {{depend store_hours}}\r\n                            <span class=\"no-link\">{{var store_hours}}</span><br>\r\n                            {{/depend}}\r\n                            {{depend store_email}}\r\n                            <b>Email:</b> <a href=\"mailto:{{var store_email}}\">{{var store_email}}</a>\r\n                            {{/depend}}\r\n                        </p>\r\n                    </td>\r\n                </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n    <tr>\r\n        <td class=\"order-details\">\r\n            <h3>Your order <span class=\"no-link\">#{{var order.increment_id}}</span></h3>\r\n            <p>Placed on {{var order.getCreatedAtFormated(\'long\')}}</p>\r\n        </td>\r\n    </tr>\r\n    <tr class=\"order-information\">\r\n        <td>\r\n            {{if order.getEmailCustomerNote()}}\r\n            <table cellspacing=\"0\" cellpadding=\"0\" class=\"message-container\">\r\n                <tr>\r\n                    <td>{{var order.getEmailCustomerNote()}}</td>\r\n                </tr>\r\n            </table>\r\n            {{/if}}\r\n            {{layout handle=\"sales_email_order_items\" order=$order}}\r\n            <table cellpadding=\"0\" cellspacing=\"0\" border=\"0\">\r\n                <tr>\r\n                    <td class=\"address-details\">\r\n               